    '.ts': 'typescript',
    '.tsx': 'tsx', // Specifically map TSX
    '.py': 'python',
    '.java': 'java',
    '.c': 'c',
    '.cpp': 'cpp',
    '.cxx': 'cpp',
    '.cc': 'cpp',
    '.h': 'c',
